iterativeInorder(root)
 s = emptyStack
current = root
do {
		while(current != null)     
		{ 	/* Travel down left branches as far as possible 		saving pointers to nodes passed in the stack*/
			push(s, current)
			current = current->left
		} //At this point, the left subtree is empty
		poppedNode = pop(s)		
		print poppedNode ->info	 //visit the node
		current = poppedNode ->right //traverse right subtree
} while(!isEmpty(s) or current != null)

